processExons <- function(filename = "human_seq_gene.md"){	if (file.exists(filename) == FALSE){		print("Souce file specified doesn't exist!")	} 	else 	{		x <- read.delim(filename, stringsAsFactors=FALSE)		y <- x[x$group_label=="Primary Assembly", ]		y <- y[y$feature_type=="CDS" | y$feature_type=="UTR",]		y <- y[,c("chromosome","chr_start","chr_stop","chr_orient","feature_id")]		for(i in 1:22){			index <- y$chromosome %in% i			y$chromosome[index] <- rep(paste("chr",i,sep=""),sum(index))		}				index <- y$chromosome %in% "X"		y$chromosome[index] <- rep("chrX",sum(index))		index <- y$chromosome %in% "Y"		y$chromosome[index] <- rep("chrY",sum(index))		NT <- unique(y$chromosome[grep("NT_",y$chromosome)])		for(i in NT){			index <- y$chromosome %in% i			i1 <- unlist(strsplit(i,"|",fixed=TRUE))			i1 <- i1[2]			y$chromosome[index] <- rep(i1,sum(index))		}		y$feature_id <- substring(y$feature_id, 8, 1000000L)		utr_cds_file <- "utr_cds.txt"		if (length(unique(y$feature_id)) > 1000000){			print("The input file contains too many genes.\n")		} else {			write.table(y,file=utr_cds_file, quote=FALSE,sep="\t",row.names=FALSE, col.names=FALSE)			.C("processExons", as.character(utr_cds_file), PACKAGE="Rsubread")		}		}}